import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

const name = "sports";
const link = "/sports";

export const fetchSports = createAsyncThunk(
  name.concat("/fetchSports"),
  async () => {
    const response = await runAjax(link.concat(".json"), "GET", {});
    return response;
  }
);

export const postSport = createAsyncThunk(
  name.concat("/postSport"),
  async (values) => {
    const response = await runAjax(link.concat(".json"), "POST", {
      sport: values,
    });
    return response;
  }
);

export const fetchSport = createAsyncThunk(
  name.concat("/fetchSport"),
  async (id) => {
    const response = await runAjax(link.concat("/" + id + ".json"), "GET", {});
    return response;
  }
);

export const updateSport = createAsyncThunk(
  name.concat("/updateSport"),
  async (params) => {
    const response = await runAjax(
      link.concat("/" + params.id + ".json"),
      "PATCH",
      { sport: params.values }
    );
    return response;
  }
);

export const deleteSport = createAsyncThunk(
  name.concat("/deleteSport"),
  async (id) => {
    await runAjax(link.concat("/" + id + ".json"), "DELETE", {});
    return id;
  }
);

export const sportsSlice = createSlice({
  name: name,
  initialState: {
    sports: [],
    status: "idle",
    error: null,
    selected: null,
  },
  reducers: {},
  extraReducers: {
    [fetchSports.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSports.fulfilled]: (state, action) => {
      state.sports = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSports.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [postSport.fulfilled]: (state, action) => {
      state.sports.push(action.payload.data);
    },
    [postSport.rejected]: (state, action) => {},
    [fetchSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [updateSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [updateSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [deleteSport.rejected]: (state, action) => {
      state.status = "failed";
    },
    [deleteSport.fulfilled]: (state, action) => {
      state.status = "succeeded";
    },
  },
});
