import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk, createAction } from "@reduxjs/toolkit";

export const dismissAlert = createAction("alerts/dismissAlert");

function createFetch(name = "") {
  return createAsyncThunk(name.concat(`/fetch_${name}`), async () => {
    const response = await runAjax(`/${name}.json`, "GET", {});
    return response;
  });
}

function createPost(name = "") {
  return createAsyncThunk(name.concat(`/post_${name}`), async (values) => {
    const response = await runAjax("/".concat(`${name}.json`), "POST", {
      [name]: values,
    });
    return response;
  });
}

function createGet(name = "") {
  return createAsyncThunk(name.concat(`/post_${name}`), async (id) => {
    const response = await runAjax("/".concat(`${name}/${id}.json`), "GET", {});
    return response;
  });
}

function createUpdate(name = "") {
  return createAsyncThunk(name.concat(`/post_${name}`), async (values) => {
    const response = await runAjax("/".concat(`${name}.json`), "PATCH", {
      [name]: values,
    });
    return response;
  });
}

function createDelete(name = "") {
  return createAsyncThunk(name.concat(`/post_${name}`), async (values) => {
    const response = await runAjax("/".concat(`${name}.json`), "DELETE", {
      [name]: values,
    });
    return response;
  });
}
