"use strict";

import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

function AppView(props) {

  return (
    <Form>
      <Form.Group controlId="sport">
        <Form.Label>Sport:</Form.Label>
        <Form.Control as="select" name="selectedSport">
          {this.sportOptions()}
        </Form.Control>
      </Form.Group>
      <Button variant="primary">Next Page</Button>
      <Button onClick={this.props.onGetSports}>Click</Button>;
    </Form>
  );
}

function sportOptions() {
  return this.props.coverages.sports.map((sport, index) => {
    return (
      <option key={index} value={sport.id}>
        {sport.name}
      </option>
    );
  });
}

export default AppView;
