import { combineReducers } from "redux";
import ActionTypes from "./constants/ActionTypes";
import { fetchSports } from "./actions/Actions";

function sports(
  state = {
    sports: [],
  },
  action
) {
  switch (action.type) {
    case ActionTypes.REQUEST_SPORTS:
      return Object.assign({}, state, {});
    case ActionTypes.RECEIVE_SPORTS:
      return Object.assign({}, state, {
        sports: action.sports,
      });
    case ActionTypes.POST_SPORT:
      console.log(action.sport)
      return Object.assign({}, state, {
        sports: state.sports.concat(action.sport),
      });
    case fetchSports.pending:
      console.log("Pending");
    default:
      return state;
  }
}

const reducer = combineReducers({ sports });

export default reducer;
