export async function runAjax(
  link,
  method = "GET",
  data = {}
  // callback = () => {
  //   this.getObjects();
  // }
) {
  let options;
  if (method === "GET") {
    options = {
      method: "GET",
    };
  } else {
    options = {
      method: method,
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "same-origin",
    };
  }

  let response = await fetch(link, options);

  if (!response.ok) {
    throw response;
  }
  if (method === "DELETE") { return }
  return response.json()

