"use strict";

import { ReduceStore } from "flux/utils";
import ActionTypes from "./action_types";
import Dispatcher from "./dispatcher";

class Store extends ReduceStore {
  constructor() {
    super(Dispatcher);
  }

  getInitialState() {
    return {
      sports: [],
    };
  }

  reduce(state, action) {
    switch (action.action.actionTypes) {
      case ActionTypes.RECEIVE_SPORTS:
        var newState = Object.assign({}, state);
        newState.sports = action.action.response;
        console.log(newState);
        return newState;

      default:
        return state;
    }
  }
}

export default new Store();
