import React, { Component } from "react";
import PropTypes from "prop-types";
import GeneralTable from "../components/GeneralTable";
import NewObject from "../components/NewObject";
import { Card, Button } from "react-bootstrap";
import "../App.css";

export default class ListStructure extends Component {
  state = {
    modal_new: false,
  };

  switchModal = (name) => {
    const modal = name;
    this.setState((prevState) => ({
      [modal]: !prevState[modal],
    }));
  };

  capitalize = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  render() {
    return (
      <>
        <Card>
          <Card.Header></Card.Header>
          <Card.Title style={{ marginTop: "10px" }}>
            All {this.capitalize(this.props.plural)}
          </Card.Title>
          <Card.Body>
            <GeneralTable
              objects={this.props.objects}
              showObjects={this.props.showObjects}
              tableHeaders={this.props.tableHeaders}
            />
          </Card.Body>
          <Card.Footer>
            <Button
              className="btn btn-theme float-right"
              variant="primary"
              onClick={(slot) => this.switchModal("modal_new")}
            >
              New {this.capitalize(this.props.name)}
            </Button>
          </Card.Footer>
        </Card>
        <NewObject
          show={this.state.modal_new}
          switchModal={this.switchModal}
          formHelpers={this.props.formHelpers}
          form={this.props.form}
        />
      </>
    );
  }
}

ListStructure.propTypes = {};
