import { combineReducers } from "redux";

import "../constants/ActionTypes";

function sports(
  state = {
    sports: [],
  },
  action
) {
  switch (action.type) {
    case REQUEST_SPORTS:
      return Object.assign({}, state, {});
    case RECEIVE_SPORTS:
      return Object.assign({}, state, {
        sports: action.posts,
      });
    default:
      return state;
  }
}

const reducer = combineReducers({ sports });

export default reducer;
