export async function runAjax(link, method = "GET", data = {}) {
  let options;
  if (method === "GET") {
    options = {
      method: "GET",
    };
  } else {
    options = {
      method: method,
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "same-origin",
    };
  }

  let response = await fetch(link, options);

  if (!response.ok) {
    var res = response.json()
    console.log(res);
    return res
  }
  // if (method === "DELETE") {
  //   return "Success";
  // }
  return response.json();
}
