//   }
// }
const initialState = { sports: [], status: "idle", error: null };
const sportsSlice = createSlice({
  name: "sports",
  initialState,
  reducers: {},
  extraReducers: {
    [fetchSports.Pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSports.fulfilled]: (state, action) => {
      state.sports = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSports.rejected]: (state, action) => {
      var currentTime = new Date().getTime();

      while (currentTime + 2000 >= new Date().getTime()) {}

      state.error = action.error;
      state.status = "failed";
    },
  },
});

// const reducer = combineReducers({ sportsSlice.reducer });

export default sportsSlice.reducer;
