import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

const name = "leagues";
const link = "/leagues";

export const fetchLeagues = createAsyncThunk(
  name.concat("/fetchLeagues"),
  async () => {
    const response = await runAjax(link.concat(".json"), "GET", {});
    return response;
  }
);

export const postLeague = createAsyncThunk(
  name.concat("/postLeague"),
  async (values) => {
    const response = await runAjax(link.concat(".json"), "POST", {
      league: values,
    });
    return response;
  }
);

export const fetchLeague = createAsyncThunk(
  name.concat("/fetchLeague"),
  async (id) => {
    const response = await runAjax(link.concat("/" + id + ".json"), "GET", {});
    return response;
  }
);

export const updateLeague = createAsyncThunk(
  name.concat("/updateLeague"),
  async (params) => {
    const response = await runAjax(
      link.concat("/" + params.id + ".json"),
      "PATCH",
      { sport: params.values }
    );
    return response;
  }
);

export const deleteLeague = createAsyncThunk(
  name.concat("/deleteLeague"),
  async (id) => {
    await runAjax(link.concat("/" + id + ".json"), "DELETE", {});
    return id;
  }
);

export const leaguesSlice = createSlice({
  name: name,
  initialState: {
    leagues: [],
    status: "idle",
    error: null,
    selected: null,
  },
  reducers: {},
  extraReducers: {
    [fetchSports.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSports.fulfilled]: (state, action) => {
      state.leagues = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSports.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [postSport.fulfilled]: (state, action) => {
      state.leagues.push(action.payload.data);
    },
    [postSport.rejected]: (state, action) => {},
    [fetchSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [updateSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [updateSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [deleteSport.rejected]: (state, action) => {
      state.status = "failed";
    },
    [deleteSport.fulfilled]: (state, action) => {
      state.status = "succeeded";
    },
  },
});
