export function runAjax(
  link,
  method = "GET",
  data = {},
  // callback = () => {
  //   this.getObjects();
  // }
) {
  let options;
  if (method === "GET") {
    options = {
      method: "GET",
    };
  } else {
    options = {
      method: method,
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "same-origin",
    };
  }

  fetch(link, options)
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    // .then((result) => {
