import React from "react";
import { Form, Row, Col } from "react-bootstrap";

export function sportForm(values, handleChange, errors) {
  return (
    <Row>
      <Form.Group as={Col}>
        <Form.Label>Name:</Form.Label>
        <Form.Control
          type="text"
          name="name"
          value={values.name}
          onChange={handleChange}
          isInvalid={!!errors.name}
        />
        <Form.Control.Feedback type="invalid">
          {errors.name}
        </Form.Control.Feedback>
      </Form.Group>
    </Row>
  );
}

const objectOptions = (objects) => {
  return objects.map((object, index) => {
    return (
      <option key={index} value={index}>
        {object.attributes.name}
      </option>
    );
  });
};

export function leagueForm(values, handleChange, errors, sports) {
  return (
    <>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>Name:</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={values.name}
            onChange={handleChange}
            isInvalid={!!errors.name}
          />
          <Form.Control.Feedback type="invalid">
            {errors.name}
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group as={Col}>
          <Form.Label>Level:</Form.Label>
          <Form.Control
            type="text"
            name="level"
            value={values.level}
            onChange={handleChange}
            isInvalid={!!errors.level}
          />
          <Form.Control.Feedback type="invalid">
            {errors.level}
          </Form.Control.Feedback>
        </Form.Group>
      </Row>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>Sport:</Form.Label>
          <Form.Control
            as="select"
            name="sport_index"
            value={values.sport_index}
            onChange={handleChange}
            isInvalid={!!errors.sport_index}
          >
            {objectOptions(sports)}
          </Form.Control>
        </Form.Group>
        <Form.Control.Feedback type="invalid">
          {errors.sport_index}
        </Form.Control.Feedback>
      </Row>
    </>
  );
}

export function clubForm(values, handleChange, errors, leagues) {
  return (
    <>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>Name:</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={values.name}
            onChange={handleChange}
            isInvalid={!!errors.name}
          />
          <Form.Control.Feedback type="invalid">
            {errors.name}
          </Form.Control.Feedback>{" "}
        </Form.Group>
        <Form.Group as={Col}>
          <Form.Label>Code:</Form.Label>
          <Form.Control
            type="text"
            name="abbreviation"
            value={values.abbreviation}
            onChange={handleChange}
          />
        </Form.Group>
      </Row>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>League:</Form.Label>
          <Form.Control
            as="select"
            name="league_id"
            value={values.league_id}
            onChange={handleChange}
          >
            {objectOptions(leagues)}
          </Form.Control>
        </Form.Group>
      </Row>
    </>
  );
}

export function companyForm(values, handleChange, errors) {
  return (
    <>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>Name:</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={values.name}
            onChange={handleChange}
            isInvalid={!!errors.name}
          />
          <Form.Control.Feedback type="invalid">
            {errors.name}
          </Form.Control.Feedback>
        </Form.Group>
      </Row>
    </>
  );
}

export function categoryForm(values, handleChange, errors) {
  return (
    <>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>Name:</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={values.name}
            onChange={handleChange}
            isInvalid={!!errors.name}
          />
          <Form.Control.Feedback type="invalid">
            {errors.name}
          </Form.Control.Feedback>
        </Form.Group>
      </Row>
    </>
  );
}

export function carrierForm(values, handleChange, errors, leagues) {
  return (
    <>
      <Row>
        <Form.Group as={Col}>
          <Form.Label>Name:</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={values.name}
            onChange={handleChange}
            isInvalid={!!errors.name}
          />
          <Form.Control.Feedback type="invalid">
            {errors.name}
          </Form.Control.Feedback>
        </Form.Group>
      </Row>
    </>
  );
}

export function objectForm(values, handleChange, errors, leagues) {
  return <></>;
}
