import ActionTypes from "../constants/ActionTypes";
import { runAjax } from "../utils/APIUtils";

function requestSports() {
  return {
    type: ActionTypes.REQUEST_SPORTS,
  };
}

function receiveSports(json) {
  return {
    type: ActionTypes.RECEIVE_SPORTS,
    sports: json,
  };
}

export function fetchSports() {
  return (dispatch) => {
    dispatch(requestSports());
    return run_ajax("http://localhost:3000/sports.json", "GET", {}, (json) => {
      console.log(json);
      dispatch(receiveSports(json));
    });
  };
}

// return run_ajax(
//   "http://www.localhost:3000/sports.json",
//   "GET",
//   {},
//   (json) => dispatch(receiveSports(json))
// );

function run_ajax(
  link,
  method = "GET",
  data = {},
  callback = () => {
    this.getObjects();
  }
) {
  let options;
  if (method === "GET") {
    options = {
      method: "GET",
    };
  } else {
    options = {
      method: method,
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "same-origin",
    };
  }

  fetch(link, options)
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((result) => {
      callback(result);
    })
    .catch((error) => {
      if (error.statusText) {
        this.setState({ error: error });
      }
      callback(error);
    });
}
