import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk } from "@reduxjs/toolkit";

const url = "http://localhost:3000/sports.json";

export const fetchSports = createAsyncThunk("sports/fetchSports", async () => {
  const response = await runAjax(url, "GET", {});
  return response;
});

export const postSport = createAsyncThunk(
  "sports/postSport",
  async (values) => {
    const new_sport = {
      name: values.name,
    };
    const response = await runAjax(url, "POST", { sport: new_sport });
    return response;
  }
);

export const fetchSport = createAsyncThunk("sports/fetchSport", async (id) => {
  const response = await runAjax(url.concat("/" + id), "GET", {});
  return response;
});
