import ActionTypes from "../constants/ActionTypes";
import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk } from "@reduxjs/toolkit";

function requestSports() {
  return {
    type: ActionTypes.REQUEST_SPORTS,
  };
}

function receiveSports(json) {
  return {
    type: ActionTypes.RECEIVE_SPORTS,
    sports: json,
  };
}

// export function fetchSports() {
//   return (dispatch) => {
//     dispatch(requestSports());
//     return runAjax("http://localhost:3000/sports.json", "GET", {}, (json) => {
//       dispatch(receiveSports(json.data));
//     });
//   };
// }

export const fetchSports = createAsyncThunk("sports/fetchSports", async () => {
  const response = await runAjax(
    "http://localhost:3000/sports.json",
    "GET",
    {}
  );
  return response;
});

export const postSport = createAsyncThunk(
  "sports/postSport",
  async (values) => {
    const new_sport = {
      name: values.name,
    };
    const response = await runAjax(
      "http://localhost:3000/sports.json",
      "POST",
      { sport: new_sport }
    );
    return response;
  }
);

