"use strict";

import React from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";

function AppView(props) {

  const tableHeaders = ["Name", "Associated Leagues"]

  return (
    <>
      <Table striped bordered hover>
        <thead>
          <tr>
            {tableHeaders.map((header) => (
              <th key={header}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>{showObjects(props.objects.sports)}</tbody>
      </Table>
      <Button onClick={props.onGetSports}>Click</Button>;
    </>
  );
}

function showObjects(objects) {
  return objects.map((object, index) => {
    return (
      <tr key={index}>
        <td width="200" align="left">
          <Button
            variant="link"
            href={"/" + this.state.plural + "/" + object.attributes.id}
            style={{ color: "black" }}
          >
            {object.attributes.name}
          </Button>{" "}
        </td>
        <td width="200" align="left">
          {object.attributes.leagues.length}
        </td>
      </tr>
    );
  });
};

export default AppView;
