import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const fetchLeagues = createAsyncThunk(
  "leagues/fetchLeagues",
  async () => {
    const response = await runAjax("/leagues.json", "GET", {});
    return response;
  }
);

export const postLeague = createAsyncThunk(
  "leagues/postLeague",
  async (values) => {
    const response = await runAjax("leagues.json", "POST", {
      league: values,
    });
    return response;
  }
);

export const fetchLeague = createAsyncThunk(
  "leagues/fetchLeague",
  async (id) => {
    const response = await runAjax(
      "/leagues".concat("/" + id + ".json"),
      "GET",
      {}
    );
    return response;
  }
);

export const updateLeague = createAsyncThunk(
  "leagues/updateLeague",
  async (params) => {
    const response = await runAjax(
      "leagues".concat("/" + params.id + ".json"),
      "PATCH",
      { sport: params.values }
    );
    return response;
  }
);

export const deleteLeague = createAsyncThunk(
  "leagues/deleteLeague",
  async (id) => {
    await runAjax("/leagues".concat("/" + id + ".json"), "DELETE", {});
    return id;
  }
);

export const leaguesSlice = createSlice({
  name: "leagues",
  initialState: {
    leagues: [],
    status: "idle",
    error: null,
    selected: null,
  },
  reducers: {},
  extraReducers: {
    [fetchLeagues.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchLeagues.fulfilled]: (state, action) => {
      state.leagues = action.payload.data;
      state.status = "succeeded";
    },
    [fetchLeagues.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [postLeague.fulfilled]: (state, action) => {
      state.leagues.push(action.payload.data);
    },
    [postLeague.rejected]: (state, action) => {},

    [postSport.fulfilled]: (state, action) => {
      state.sports.push(action.payload.data);
    },
    [postSport.rejected]: (state, action) => {},
    [fetchSport.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [updateSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [updateSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [deleteSport.fulfilled]: (state, action) => {
      state.status = "succeeded";
    },
  },
});
