import { combineReducers } from "redux";
import ActionTypes from "./constants/ActionTypes";
import { fetchSports, postSport, fetchSport, updateSport} from "./actions/Actions";
import { createSlice, createAction } from "@reduxjs/toolkit";

const initialState = {
  sports: [],
  status: "idle",
  error: null,
  selected: null,
};
const sportsSlice = createSlice({
  name: "sports",
  initialState,
  reducers: {},
  extraReducers: {
    [fetchSports.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSports.fulfilled]: (state, action) => {
      state.sports = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSports.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [postSport.fulfilled]: (state, action) => {
      state.sports = state.sports.concat(action.payload.data);
    },
    [postSport.rejected]: (state, action) => {},
    [fetchSport.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [updateSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [updateSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
  },
});

// const reducer = combineReducers({ sportsSlice.reducer });

export default sportsSlice.reducer;
