import { runAjax } from "../utils/APIUtils";
import { createAsyncThunk, createAction } from "@reduxjs/toolkit";

const url = "http://localhost:3000/sports";

export const fetchSports = createAsyncThunk("sports/fetchSports", async () => {
  const response = await runAjax("/sports.json", "GET", {});
  return response;
});

export const fetchLeagues = createAsyncThunk(
  "leagues/fetchLeagues",
  async () => {
    const response = await runAjax("/leagues.json", "GET", {});
    return response;
  }
);
// export const fetchObjects = createAsyncThunk("general/fetchObjects", async (name) => {
//   const response = await runAjax(url.concat(name + ".json"), "GET", {});
//   return response;
// });

export const postSport = createAsyncThunk(
  "sports/postSport",
  async (values) => {
    const response = await runAjax("sports.json", "POST", {
      sport: values,
    });
    return response;
  }
);

export const fetchSport = createAsyncThunk("sports/fetchSport", async (id) => {
  const response = await runAjax(url.concat("/" + id + ".json"), "GET", {});
  return response;
});

export const updateSport = createAsyncThunk(
  "sports/updateSport",
  async (params) => {
    const response = await runAjax(
      url.concat("/" + params.id + ".json"),
      "PATCH",
      { sport: params.values }
    );
    return response;
  }
);

export const deleteSport = createAsyncThunk(
  "sports/deleteSport",
  async (id) => {
    const response = await runAjax(
      url.concat("/" + id + ".json"),
      "DELETE",
      {}
    );
    return id;
  }
);

export const dismissAlert = createAction("alerts/dismissAlert");
