import React, { Component } from "react";
import PropTypes from "prop-types";
import { Modal, Form, Button} from "react-bootstrap";
import { Formik } from "formik";
import { SportSchema } from "../utils/Schemas";
import { sportForm } from "../utils/Forms";

class NewObject extends React.Component {
  handleClose = (name) => {
    this.props.switchModal(name);
  };

  render() {
    return (
      <Modal
        show={this.props.show}
        onHide={() => this.handleClose("modal_new")}
      >
        <Modal.Header closeButton>
          <Modal.Title>New League</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Formik
            validationSchema={SportSchema}
            onSubmit={(values) => this.handleCreate(values)}
            initialValues={{
              name: "",
              level: "",
              sport_id: "",
            }}
          >
            {({
              handleSubmit,
              handleChange,
              values,
              errors,
            }) => (
              <Form noValidate onSubmit={handleSubmit}>
                {sportForm(values, handleChange, errors)}
                <Button
                  type="submit"
                  variant="primary"
                  className="btn btn-theme float-right"
                >
                  Create League
                </Button>
              </Form>
            )}
          </Formik>
        </Modal.Body>
      </Modal>
    );
  }
}

export default NewObject;
