import fetch from "cross-fetch";
import ActionTypes from "../constants/ActionTypes";

function requestSports() {
  return {
    type: ActionTypes.REQUEST_SPORTS,
  };
}

function receiveSports(json) {
  return {
    trype: ActionTypes.RECEIVE_SPORTS,
    sports: json,
  };
}

export function fetchSports() {
  return (dispatch) => {
    dispatch(requestSports());
    return fetch("http://www.localhost:3000/sports.json", options = { method: "GET" })
      .then((response) => response.json())
      .then((json) => dispatch(receiveSports(json)));
  };
}

