import React, { Component } from "react";
import { connect } from "react-redux";
import { Alert } from "react-bootstrap";
import { dismissAlert } from "../actions/Actions";

class AlertsContainer extends Component {

  showAlerts = () => {
    return this.props.alerts.map((alert, index) => {
      return (
        <Alert key={index} variant={alert.variant} onClose={() => dismissAlert(index)} dismissible>
          {alert.message}
        </Alert>
      );
    });
  };

  render() {
    return <>{this.showAlerts()}</>;
  }
}

AlertsContainer.propTypes = {};

function mapStateToProps(state) {
  const { alerts } = state.alerts;
  return { alerts };
}

export default connect(mapStateToProps)(AlertsContainer);
