import ActionTypes from "../constants/ActionTypes";
import { runAjax } from "../utils/APIUtils";

function requestSports() {
  return {
    type: ActionTypes.REQUEST_SPORTS,
  };
}

function receiveSports(json) {
  return {
    type: ActionTypes.RECEIVE_SPORTS,
    sports: json,
  };
}

export function fetchSports() {
  return (dispatch) => {
    dispatch(requestSports());
    return run_ajax("http://localhost:3000/sports.json", "GET", {}, (json) => {
      console.log(json);
      dispatch(receiveSports(json));
    });
  };
}
