import { combineReducers } from "redux";
import ActionTypes from "./constants/ActionTypes";
import {
  fetchSports,
  postSport,
  fetchSport,
  updateSport,
  dismissAlert,
} from "./actions/Actions";
import { createSlice, createAction } from "@reduxjs/toolkit";

const initialState = {
  sports: [],
  status: "idle",
  error: null,
  selected: null,
};
const sportsSlice = createSlice({
  name: "sports",
  initialState,
  reducers: {},
  extraReducers: {
    [fetchSports.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSports.fulfilled]: (state, action) => {
      state.sports = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSports.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [postSport.fulfilled]: (state, action) => {
      state.sports.push(action.payload.data);
    },
    [postSport.rejected]: (state, action) => {},
    [fetchSport.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [fetchSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [updateSport.fulfilled]: (state, action) => {
      state.selected = action.payload.data;
      state.status = "succeeded";
    },
    [updateSport.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
  },
});

const fulfilled = (state) =>
  state.alerts.push({
    message: "The object was successfully added to the system.",
    variant: "success",
  });

const rejected = (state) =>
  state.alerts.push({
    message: "The object could not be added to the system at this time.",
    variant: "danger",
  });

const alertsSlice = createSlice({
  name: "alerts",
  initialState: { alerts: [] },
  reducers: {},
  extraReducers: {
    [updateSport.fulfilled]: (state, action) => {
      fulfilled(state);
    },
    [updateSport.rejected]: (state, action) => {
      rejected(state);
    },
    [dismissAlert]: (state, action) => {
      state.alerts.splice(action.payload, 1);
    },
  },
});

const reducer = combineReducers({
  sports: sportsSlice.reducer,
  alerts: alertsSlice.reducer,
});

export default reducer;
