import React from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { Form, Col, Row, Card } from "react-bootstrap";
import { Formik } from "formik";
import Button from "react-bootstrap/lib/Button";
import DatePicker from "react-bootstrap-date-picker";

export default class CoverageDetails extends React.Component {
  map = (values, object) => {
    var new_values = {};
    for (var key in values) {
      new_values[key] = object[key];
    }
    return new_values;
  };

  render() {
    return (
      <>
        <Card>
          <Card.Header></Card.Header>
          <Card.Title style={{ marginTop: "10px" }}>
            Coverage Details
          </Card.Title>
          <Card.Body>
            <Formik
              validationSchema={this.props.formHelpers.schema}
              onSubmit={console.log("Update")}
              initialValues={this.map(
                this.props.formHelpers.initialValues,
                this.props.object.attributes
              )}
              enableReinitialize={true}
            >
              {({
                handleSubmit,
                handleChange,
                setFieldValue,
                values,
                dirty,
              }) => (
                <Form noValidate onSubmit={handleSubmit}>
                  <Row>
                    <Form.Group as={Col}>
                      <Form.Label>Club:</Form.Label>
                      <Form.Control
                        type="text"
                        name="club"
                        value={values.club}
                        disabled
                      />
                    </Form.Group>

                    <Form.Group as={Col}>
                      <Form.Label>Group:</Form.Label>
                      <Form.Control
                        type="text"
                        name="group"
                        value={values.group}
                        disabled
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col}>
                      <Form.Label>Category:</Form.Label>
                      <Form.Control
                        type="text"
                        name="category"
                        value={values.category}
                        disabled
                      />
                    </Form.Group>

                    <Form.Group as={Col}>
                      <Form.Label>Sub Category:</Form.Label>
                      <Form.Control
                        type="text"
                        name="sub_category"
                        value={values.sub_category}
                        disabled
                      />
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col}>
                      <Form.Label>Carriers:</Form.Label>
                      <Form.Control
                        as="select"
                        multiple
                        name="carriers"
                        value={values.carriers}
                        onChange={(event) =>
                          setFieldValue(
                            "carriers",
                            Array.from(event.target.selectedOptions, (option) =>
                              Number(option.value)
                            )
                          )
                        }
                      >
                        {this.showCarriers()}
                      </Form.Control>
                    </Form.Group>

                    <Form.Group as={Col}>
                      <Form.Label>Brokers:</Form.Label>
                      <Form.Control
                        as="select"
                        multiple
                        name="brokers"
                        value={values.brokers}
                        onChange={(event) =>
                          setFieldValue(
                            "brokers",
                            Array.from(event.target.selectedOptions, (option) =>
                              Number(option.value)
                            )
                          )
                        }
                      >
                        {this.showBrokers()}
                      </Form.Control>
                    </Form.Group>
                  </Row>
                  <Form.Group>
                    <Form.Label>Notes:</Form.Label>
                    <Form.Control
                      type="text"
                      name="notes"
                      defaultValue={this.state.object?.attributes.notes}
                      onChange={handleChange}
                    />
                  </Form.Group>
                  <Row>
                    <Form.Group as={Col}>
                      <Form.Label>{"Start Date:"}</Form.Label>
                      {/* <DatePicker
                        name="start_date"
                        selected={values.start_date}
                        onChange={(val) => setFieldValue("start_date", val)}
                      /> */}
                    </Form.Group>

                    <Form.Group as={Col}>
                      <Form.Label>{"Ending Date:"}</Form.Label>
                      {/* <DatePicker
                        name="end_date"
                        selected={values.end_date}
                        onChange={(val) => setFieldValue("end_date", val)}
                      /> */}
                    </Form.Group>
                  </Row>
                  <Row>
                    <Form.Group as={Col}>
                      <Form.Check
                        type="checkbox"
                        name="has_coverage_line"
                        label={"Has Coverage Line"}
                        checked={values.has_coverage_line}
                        value={values.has_coverage_line}
                        onChange={handleChange}
                      />
                    </Form.Group>

                    <Form.Group as={Col}>
                      <Form.Check
                        type="checkbox"
                        name="verified"
                        label={"Verified"}
                        checked={values.verified}
                        value={values.verified}
                        onChange={handleChange}
                      />
                    </Form.Group>
                  </Row>
                  {dirty == true ? (
                    <Button
                      type="submit"
                      className="btn btn-theme float-right"
                      variant="primary"
                      style={{ marginRight: "10px" }}
                    >
                      Update Coverage
                    </Button>
                  ) : (
                    ""
                  )}
                </Form>
              )}
            </Formik>
          </Card.Body>
          <Card.Footer></Card.Footer>
        </Card>
      </>
    );
  }
}

CoverageDetails.propTypes = {};
