import React from "react";
import { Table } from "react-bootstrap";

export default class GeneralTable extends React.Component {
  render() {
    const { objects, showObjects, tableHeaders } = this.props;
    if (objects.length === 0) {
      return this.props.message || "There is no data to display at this time.";
    }
    return (
      <>
        <Table striped bordered hover>
          <thead>
            <tr>
              {tableHeaders.map((header) => (
                <th key={header}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>{showObjects(objects)}</tbody>
        </Table>
      </>
    );
  }
}

GeneralTable.propTypes = {
  objects: PropTypes.arrayOf(PropTypes.object).isRequired,
  showObjects: PropTypes.func.isRequired,
  tableHeaders: PropTypes.arrayOf(PropTypes.string).isRequired,
  message: PropTypes.string,
};
