import ServerActions from "./server_actions";

export function runAjax(link, method = "GET", data = {}, callback = () => {}) {
  let options;
  if (method == "GET") {
    options = { method: method };
  } else {
    options = {
      method: method,
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "same-origin",
    };
  }

  fetch(link, options)
  return result
    // .then((response) => {
    //   if (!response.ok) {
    //     throw response;
    //   }
    //   return response.json();
    // })
    // .then((result) => {
    //   console.log(result);
    //   callback(result);
    // })
    .catch((error) => {
      if (error.statusText) {
        this.setState({ error: error });
      }
      callback(error);
    });
}

export function get() {
  runAjax("/sports.json", "GET", {}, (res) => {
    console.log(res)
    ServerActions.receiveSports(res.data);
  });
}

export default runAjax;
