import { combineReducers } from "redux";
import ActionTypes from "./constants/ActionTypes";
import {
  fetchSports,
  fetchLeagues,
  postSport,
  postLeague,
  fetchSport,
  updateSport,
  deleteSport,
  dismissAlert,
} from "./actions/Actions";
import { createSlice, createAction } from "@reduxjs/toolkit";
import { sportsSlice } from "./data/sports"

const leaguesSlice = createSlice({
  name: "leagues",
  initialState: {
    leagues: [],
    status: "idle",
    error: null,
    selected: null,
  },
  reducers: {},
  extraReducers: {
    [fetchLeagues.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchLeagues.fulfilled]: (state, action) => {
      state.leagues = action.payload.data;
      state.status = "succeeded";
    },
    [fetchLeagues.rejected]: (state, action) => {
      state.error = action.error;
      state.status = "failed";
    },
    [postLeague.fulfilled]: (state, action) => {
      state.leagues.push(action.payload.data);
    },
    [postLeague.rejected]: (state, action) => {},
  },
});

const fulfilledNew = (state) => {
  let alerts = state.alerts.concat({
    message: "The object was successfully added to the system.",
    variant: "success",
  });
  return { alerts };
};

const fulfilledUpdated = (state) =>
  state.alerts.push({
